import React, { useEffect, useRef } from 'react';
import Container from '../components/container';
import styled from 'styled-components';

const Ul = styled.ul`
  position: absolute;
  display: flex;
  flex-direction: row;
  top: 100px;
  opacity: 0;
  transition: 0.5s;
`;
const Li = styled.li`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  list-style: none;
`;

const Upload = () => {
  const canvas = useRef();

  return (
    <div>
      <canvas ref={canvas}></canvas>
      <div class="palette">
        <ul class="colors">
          <Li style={{ backgroundColor: '#00a8ff' }}></Li>
          <Li style={{ backgroundColor: '#9c88ff' }}></Li>
          <Li style={{ backgroundColor: '#fbc531' }}></Li>
          <Li style={{ backgroundColor: '#e84118' }}></Li>
          <Li style={{ backgroundColor: '#ffffff' }}></Li>
        </ul>
        <input
          class="strokeWidth"
          type="range"
          id="jsRange"
          min="0.1"
          max="10"
          value="2.5"
          step="0.1"
        />
      </div>
    </div>
  );
};

export default Upload;
