import React, { useEffect } from 'react';
import Link from 'next/link';
import { useSelector, useDispatch } from 'react-redux';
import Router from 'next/Router';
import Container, { GalleryContainer } from '../components/container';
import Text from '../components/text';
import { Button } from '../components/uiComponent';
import { EDITING_PROFILE_ON } from '../reducers/user';
import EditProfile from '../components/editProfile';

const Mypage = () => {
  const { me, isLoggedIn, likeList } = useSelector((state) => state.user);
  const dispatch = useDispatch();

  useEffect(() => {
    if (!isLoggedIn) {
      Router.push('/');
    }
  }, [isLoggedIn]);

  const editProfile = (e) => {
    e.preventDefault();
    dispatch({
      type: EDITING_PROFILE_ON,
    });
  };

  return (
    <>
      <EditProfile />
      <Container flexDirection="column">
        <h3 style={{ marginBottom: '70px' }}>마이페이지</h3>
        <Button onClick={editProfile}>Edit Profile</Button>
        <div
          style={{
            display: 'flex',
            flexDirection: 'column',
            width: '500px',
            minHeight: '600px',
            textAlign: 'left',
          }}
        >
          <Text fontSize="medium" style={{ marginBottom: '8px' }}>
            작가
          </Text>
          <Text fontSize="big" bold>
            {me ? me.writer : ''}
          </Text>
          <br />
          <Text fontSize="medium" style={{ marginBottom: '8px' }}>
            작가 소개
          </Text>
          <Text fontSize="big" bold>
            {me ? me.userInfo : ''}
          </Text>
        </div>
      </Container>
      <GalleryContainer>
        {likeList ? (
          likeList.map((item, id) => (
            <Link key={id} href="/p/[imgDetail]" as={`/p/${item._id}`}>
              <a>
                <PhotoView
                  creater={item.title}
                  url={`http://localhost:5000/${item.fileUrl}`}
                />
              </a>
            </Link>
          ))
        ) : (
          <></>
        )}
      </GalleryContainer>
    </>
  );
};

export default Mypage;
