import React, { useEffect, useRef, useState } from 'react';
import Container from '../components/container';
import styled from 'styled-components';

const Ul = styled.ul`
  position: absolute;
  display: flex;
  flex-direction: row;
  transition: 0.5s;
`;
const Li = styled.li`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  list-style: none;
`;
const Canvas = styled.canvas`
  width: 700px;
  height: 700px;
  border-radius: 15px;
  background: #ffffff;
  box-shadow: 18px 18px 25px #00000057, -18px -18px 25px #ffffff;
`;

const Upload = () => {
  const [canvasState, setCanvasState] = useState({
    painter: false,
    strokeStyle: '#000',
    lineWidth: 2.5,
  });
  const canvas = useRef();

  const startPainting = () => {
    setCanvasState({
      ...canvasState,
      painter: true,
    });
  };
  const stopPainting = () => {
    setCanvasState({
      ...canvasState,
      painter: false,
    });
  };
  const onMouseMove = () => {};

  useEffect(() => {
    canvas.current.width = 700;
    canvas.current.height = 700;

    const ctx = canvas.current.getContext('2d');

    ctx.strokeStyle = canvasState.strokeStyle;
    ctx.lineWidth = canvasState.lineWidth;
    ctx.lineCap = 'round';
    ctx.lineJoin = 'round';
  }, []);

  return (
    <div>
      <Canvas
        ref={canvas}
        onMouse
        onMouseDown={startPainting}
        onMouseUp={stopPainting}
        onMouseLeave={stopPainting}></Canvas>
    </div>
  );
};

export default Upload;
