import React, { useEffect, useRef } from 'react';
import Container from '../components/container';
import styled from 'styled-components';

const Ul = styled.ul`
  position: absolute;
  display: flex;
  flex-direction: row;
  transition: 0.5s;
`;
const Li = styled.li`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  list-style: none;
`;
const Canvas = styled.canvas`
width: 700px;
  height: 700px;
  border-radius: 15px;
  background: #ffffff;
  box-shadow: 18px 18px 25px #00000057, -18px -18px 25px #ffffff;
`;

const Upload = () => {
  const canvas = useRef();

  useEffect(()=>{
    canvas.current.width = 700;
    canvas.current.height = 700;


  }, [])

  return (
    <div>
      <Canvas ref={canvas}></Canvas>
      <div class="palette">
        <Ul>
          <Li style={{ backgroundColor: '#00a8ff' }}></Li>
          <Li style={{ backgroundColor: '#9c88ff' }}></Li>
          <Li style={{ backgroundColor: '#fbc531' }}></Li>
          <Li style={{ backgroundColor: '#e84118' }}></Li>
          <Li style={{ backgroundColor: '#ffffff' }}></Li>
        </Ul>
        <input
          class="strokeWidth"
          type="range"
          id="jsRange"
          min="0.1"
          max="10"
          value="2.5"
          step="0.1"
        />
      </div>
    </div>
  );
};

export default Upload;
