import styled from 'styled-components';

const SIZES = {
	mini: '10px',
	tiny: '13px',
	small: '14px',
	medium: '15px',
	large: '16px',
	big: '19px',
	huge: '20px',
  massive: '24px',
  moreMassive:'26px'
};

const COLOR = {
	normal: '#707070'
};



const Text = styled.span`
color:${fontColor.basegray};

`;
export default Text;
