import React, { useState, useCallback } from 'react';
import styled from 'styled-components';
import { faEllipsisV } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const PopupMenu = styled.div`
  position: absolute;
  width: 250px;
  height: 500px;
  background: black;
  transition: 0.5s;
  opacity: 0;
`;

const style = {
  position: 'absolute',
  width: '250px',
  height: '500px',
  background: 'black',
  transition: '.5s',
};

const Navigation = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [popUpState, setPopUpState] = useState({
    opacity: '0',
    display: 'none',
  });

  const handleSwitch = () => {
    setMenuOpen((prev) => !prev);
    handelMenuState();
  };

  const handelMenuState = useCallback(() => {
    if (menuOpen) {
      setTimeout(() => {
        setPopUpState({
          ...popUpState,
          [display]: '',
        });
        setTimeout(() => {
          setPopUpState({
            ...popUpState,
            [opacity]: '1',
          });
        }, 10);
      }, 0);
    } else {
      setTimeout(() => {
        setPopUpState({
          ...popUpState,
          [opacity]: '0',
        });
        setTimeout(() => {
          setPopUpState({
            ...popUpState,
            [display]: 'none',
          });
        }, 10);
      }, 0);
    }
  });

  return (
    <div>
      <FontAwesomeIcon
        icon={faEllipsisV}
        size={'2x'}
        style={{ cursor: 'pointer' }}
        onClick={handleSwitch}
      />
      <PopupMenu opacity={popUpState.opacity}/>
    </div>
  );
};

export default Navigation;
