import React from 'react';

const Root = ({ Component }) => {
  return (
    <div>
      <span>레이아웃</span>
      <Component />
      <style jsx global>{`
      html, body{
          width:100%;
          height:100%;
          display:flex;
          justify-content:center;
          align-items:center;
      }
      `}</style>
    </div>
  );
};

export default Root;
