import React from 'react';
import Container from './container';
import Navigation from './navigation';
import Loading from './loading';
import { useSelector } from 'react-redux';

const AppLayout = ({ children }) => {
  const { isUserLoadding } = useSelector((state) => state.user);
  const { isLoadding } = useSelector((state) => state.drawing);

  return (
    <Container
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        minHeight: '700px',
      }}
      flexDirection="column"
    >
      <Navigation style={{ alignSelf: 'flex-start' }} />
      <Container style={{ paddingTop: '60px' }}>{children}</Container>
      {isUserLoadding || isLoadding ?(<div
        style={{
          position: 'absolute',
          width: '100%',
          height: '100%',
          background: '#33333348',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <Loading />
      </div>):(<></>)}
    </Container>
  );
};

export default AppLayout;
