import { all, delay, fork, put, takeEvery } from 'redux-saga/effects';

import {
  LOG_IN_REQUEST,
  LOG_IN_SUCCESS,
  LOG_IN_FAILURE,
} from '../reducers/user';

function loginAPI(){
}

function* login(){
    try{
        yield delay(2000)
        yield put({
            type:LOG_IN_SUCCESS
        })
    }catch(e){
        console.log(e)
        yield put({
            type:LOG_IN_FAILURE
        })
    }
}

function* watchLogin() {
    yield takeEvery(LOG_IN_REQUEST, login);
  }

export default function* userSaga(){
    yield all([fork(watchLogin)])
}
