import React, { useEffect, useRef } from 'react';
import Container from '../components/container';

const Upload = () => {
  const canvas = useRef();

  useEffect(() => {
    color.current.style.backgroundColor = 'red';
  }, []);

  return (
    <div>
      <canvas class="canvas_container jsCanvas"></canvas>
      <div class="palette">
        <ul class="colors">
          <li class="color" style="background-color: #00a8ff;"></li>
          <li class="color" style="background-color: #9c88ff;"></li>
          <li class="color" style="background-color: #fbc531;"></li>
          <li class="color" style="background-color: #e84118;"></li>
          <li
            class="color"
            style="background-color: #ffffff; border: 1px solid black;"></li>
        </ul>
        <input
          class="strokeWidth"
          type="range"
          id="jsRange"
          min="0.1"
          max="10"
          value="2.5"
          step="0.1"
        />
      </div>
    </div>
  );
};

export default Upload;
