import React, { useEffect, useRef } from 'react';
import Container from '../components/container';
import styled from 'styled-components';

const Li = styled.li`
width:50px;
height:50px;
border-radius:50%;
`

const Upload = () => {
  const canvas = useRef();

  return (
    <div>
      <canvas ref={canvas}></canvas>
      <div class="palette">
        <ul class="colors">
          <li
            class="color"
            style={{ backgroundColor: '#00a8ff', borderRadius: '50%' }}></li>
          <li
            class="color"
            style={{ backgroundColor: '#9c88ff', borderRadius: '50%' }}></li>
          <li
            class="color"
            style={{ backgroundColor: '#fbc531', borderRadius: '50%' }}></li>
          <li
            class="color"
            style={{ backgroundColor: '#e84118', borderRadius: '50%' }}></li>
          <li
            class="color"
            style={{ backgroundColor: '#ffffff', borderRadius: '50%' }}></li>
        </ul>
        <input
          class="strokeWidth"
          type="range"
          id="jsRange"
          min="0.1"
          max="10"
          value="2.5"
          step="0.1"
        />
      </div>
    </div>
  );
};

export default Upload;
