import styled from 'styled-components';

const SIZES = {
	mini: '10px',
	tiny: '13px',
	small: '14px',
	medium: '15px',
	large: '16px',
	big: '19px',
	huge: '20px',
  massive: '24px',
  moreMassive:'26px'
};

const COLOR = {
	underPrice: '#9B9B9B',
	realPrice: '#7A0000',
	review: '#6D6D6D',
	normal: '#000000'
};


const fontColor = {
  basegray: '#707070',
  black: '#000000',
};

const Text = styled.span`
color:${fontColor.basegray};

`;
export default Text;
