import React, { useEffect, useState } from 'react';
import Router from 'next/Router';
import { useSelector, useDispatch } from 'react-redux';
import Container from './container';
import { EDITING_PROFILE_REQUEST, EDITING_PROFILE_OFF } from '../reducers/user';
import { Button, Input, Textarea } from './uiComponent';

const EditProfile = ({ signUp }) => {
  const { editing, me } = useSelector((state) => state.user);
  const [info, setInfo] = useState({
    userInfo: '',
    name: '',
  });

  useEffect(()=>{},[])

  const dispatch = useDispatch();

  const handleSubmit = (e) => {
    e.preventDefault();
    const writerData = {
      userInfo: info.userInfo,
      writer: info.name,
    };
    dispatch({
      type: EDITING_PROFILE_REQUEST,
      data: { me, writerData },
    });
  };

  const modalOff = () => {
    dispatch({
      type: EDITING_PROFILE_OFF,
    });
  };

  const handleChangeInfo = (e) => {
    setInfo({
      ...info,
      userInfo: e.target.value,
    });
  };
  const handleChangename = (e) => {
    setInfo({
      ...info,
      name: e.target.value,
    });
  };

  return (
    <Container
      style={{
        position: 'absolute',
        width: '100%',
        height: '100%',
        background: '#4e4e4e6b',
        zIndex: 99999,
        top: '0',
        display: `${editing && me ? '' : 'none'}`,
      }}
    >
      <Container
        wsize="400px"
        flexDirection="column"
        style={{ borderRadius: '10px', background: '#ccc' }}
      >
        <h3>작가 프로필</h3>
        <form
          onSubmit={handleSubmit}
          style={{
            display: 'flex',
            justifyContent: 'center',
            flexDirection: 'column',
          }}
        >
          <Input
            type="text"
            value={info.name}
            onChange={handleChangename}
            placeholder="작가명"
          />
          <Textarea
            value={info.userInfo}
            onChange={handleChangeInfo}
            placeholder="작가소개"
          />
          <Container
            style={{ margin: '0', justifyContent: 'space-between' }}
            wsize="150px"
            hsize="50px"
          >
            <Button>확인</Button>
            {!signUp && <Button onClick={modalOff}>취소</Button>}
          </Container>
        </form>
      </Container>
    </Container>
  );
};

export default EditProfile;
